var debug = false

function configGetDesired(ep) {
    var desiredConfig = endpoint.storageGet(ep.key, 'desired-config')
    if (!desiredConfig) {
        if(global.values.initializeDesiredConfig) {
            var latestConfig = endpoint.storageGet(ep.key, 'latest-config')
            if(!latestConfig) {
                return undefined
            }
            desiredConfig = {values: latestConfig.values}
            endpoint.storageSet(ep.key, 'desired-config', desiredConfig)
        } else {
            return undefined
        }
    }
    return desiredConfig
}

function configSetDesired(ep, config) {
    var desiredConfig = configGetDesired(ep)

    if (!config) return

    var keys = Object.keys(config)
    keys.forEach(function(key) {
        desiredConfig.values[key] = config[key]
    })

    endpoint.storageSet(ep.key, 'desired-config', desiredConfig)
}

function configNeeded(ep, desiredConfig, config) {
    if (!desiredConfig) {
        return false
    }
    
    if (!config) {
        config = endpoint.storageGet(ep.key, 'latest-config')
    }

    if (!config) {
        log.trace('checking if config is needed: yes', {reason: 'no known config'})
        return true
    }

    var keys = Object.keys(desiredConfig.values)
    var match = true
    keys.forEach(function(key) {
        if(desiredConfig.values[key] !== config.values[key]) {
            match = false
        }
    })

    if(match) {
        if (event.endpoint.tags.config == 'update-in-progress') {
            log.trace('configuration update complete')
        }
        if (ep.tags.config && ep.tags.config != 'up-to-date') {
            endpoint.updateTags(ep.key, { config: 'up-to-date' }, null)
        }
    } else {
         if (ep.tags.config && ep.tags.config != 'update-needed') {
            endpoint.updateTags(ep.key, { config: 'update-needed' }, null)
            log.trace('configuration update needed')
        }
    }
    return !match
}

var sd = {}

if (event.data.data.accountId) {
    sd.accountId = event.data.data.accountId
}
if (event.data.data.reportingInterval) {
    sd.reportingInterval = '' + event.data.data.reportingInterval
}
if (event.data.data.readingInterval) {
    sd.readingInterval = '' + event.data.data.readingInterval
}
if (event.data.data.server) {
    sd.server = '' + event.data.data.server
}

endpoint.update(event.endpoint.key, { statusDetails: sd })

