trigger.include('Compact UDP Protocol - Library')

var debugDecode = false

var reader = new convert.binReader(event.data.payload)

function readVLString(reader) {
    var vlen = reader.readUint8()
    if(vlen==0) return
    return reader.readString(vlen)
}

function writeVLString(writer, str) {
    writer.writeUint8(str.length)
    writer.writeString(str)
}  

function rssiToCategory(rssi) {
  if (rssi >= -65) {
    return "great";
  } else if (rssi >= -75) {
    return "good";
  } else if (rssi >= -85) {
    return "fair";
  } else {
    return "poor";
  }
}

function readData(reader, pkt) {
    var dataCount = reader.readUint8()
    for (var i = 0; i < dataCount; i++) {
        var sd = {
            data_type: reader.readUint8(),
            data_version: reader.readUint8(),
            data_length: reader.readUint16()
        }

        if(debugDecode) log.trace('data field: (t:'+sd.data_type+') (v:'+sd.data_version+') (l:'+sd.data_length+')', reader.status())

        switch (sd.data_type) {
            case 1:
                var count = reader.readUint8()
                for(var i=0;i<count;i++) {
                    pkt.values[readVLString(reader)] = readVLString(reader)
                }
                break
            case 10:
                var locType = reader.readUint8()
                switch (locType) {
                    case 1: // gnss
                        pkt.loc = {
                            source: 'gnss',
                            lat: reader.readFloat32(),
                            lng: reader.readFloat32()
                        }
                        break
                    case 2: // cell
                        // get cell location
                        var cell = {
                            mcc: +readVLString(reader),
                            mnc: +readVLString(reader),
                            cells: [
                                { lacHex: readVLString(reader), cellIdHex: readVLString(reader), rssi: reader.readInt8() }
                            ]
                        }
                        try {
                            var loc = location.geolocate('cell-location', cell)
                            pkt.loc = {
                                source: 'cell',
                                lat: loc.lat,
                                lng: loc.lon
                            }
                        } catch (e) {
                            log.warn('failed to geolocate device: '+e)
                        }
                        break

                }
                break
            case 11:
                pkt.values.customer_id = convert.binToHex(reader.readBytes(reader.readUint8()))
                break
            case 20:
                pkt.values.software_version = readVLString(reader)
                pkt.values.modem_ersion = readVLString(reader)
                break
            case 21:
                var mcc = readVLString(reader)
                var mnc = readVLString(reader)
                try {
                    var net = network.decodeMccMnc(mcc, mnc)
                    pkt.values.country = net.country
                    pkt.values.operator = net.operator
                } catch (e) {
                    pkt.values.country = 'unknown'
                    pkt.values.operator = 'unknown'
                }
                pkt.values.radio_tech = readVLString(reader)
                break
            case 22:
                pkt.values.battery = reader.readUint8()
                var rssi = reader.readInt8()
                if (rssi <= 0) {
                    pkt.values.rssi = -113;
                } else if (rssi >= 31) {
                    pkt.values.rssi = -51;
                } else {
                    pkt.values.rssi = (rssi * 2) - 113;
                }
                endpoint.update(event.endpoint.key, {
                    addTags: {
                        signalQuality: rssiToCategory(pkt.values.rssi)
                    },
                    statusDetails: {
                        rssi: pkt.values.rssi,
                        battery: Math.round(pkt.values.battery / 10) * 10,
                    }
                })
            case 100:
                pkt.values.temperature = reader.readUint16() / 10.0
                pkt.values.humidity = reader.readUint16() / 10.0
                pkt.values.illumination = reader.readUint16()
                pkt.values.motion = reader.readBool()
                break
            case 101:
                var first_ts = reader.readUint32() * 1000
                var interval = reader.readUint16()

                pkt.values.readings = []
                var record_count = reader.readUint8()
                for (var i = 0; i < record_count; i++) {
                    pkt.values.readings.push({ ts: new Date(first_ts + (interval * i * 1000)), temperature: reader.readUint16() / 10.0, humidity: reader.readUint16() / 10.0 })
                }
                break
            case 102:
                pkt.values.steps = reader.readUint32()
                break
            default:
        }
        var addTags = {}
        var statusDetails = {}
        if (pkt.values.rssi) {
            addTags.signalQuality = rssiToCategory(pkt.values.rssi)
            statusDetails.rssi = pkt.values.rssi
        }
        if (pkt.values.battery) {
            statusDetails.battery = Math.round(pkt.values.battery / 10) * 10
        }
        endpoint.update(event.endpoint.key, {
            addTags: addTags,
            statusDetails: statusDetails
        })
    }
}

var desiredConfig = configGetDesired(event.endpoint)
var requestConfig = false

var pkt = {
    ver: reader.readUint8(),
    cmd: reader.readString(2),
    txnId: reader.readUint16(),
    imei: convert.binToHex(reader.readBytes(reader.readUint8())),
    ts: new Date(reader.readUint32() * 1000),
    values: {},
}


if (pkt.imei[0] == '0') {
    pkt.imei = pkt.imei.substring(1)
}

if(debugDecode) log.trace('header decoded', pkt)

readData(reader, pkt)

if(debugDecode) log.trace('data decoded', pkt)

switch (pkt.cmd) {
    case 'T':
        pkt.desc = 'telemetry'
        requestConfig = configNeeded(event.endpoint, desiredConfig, undefined)
        break
    case 'M+':
        pkt.desc = 'motion-start'
        requestConfig = configNeeded(event.endpoint, desiredConfig, undefined)
        break
    case 'M-':
        pkt.desc = 'motion-stop'
        requestConfig = configNeeded(event.endpoint, desiredConfig, undefined)
        break
    case 'C':
        pkt.desc = 'config'
        requestConfig = configNeeded(event.endpoint, desiredConfig, pkt)
        break
    case 'P+':
        pkt.desc = 'power-on'
        break
    default:
        pkt.desc = 'unknown'
        break
}

endpoint.storageSet(event.endpoint.key, 'latest-' + pkt.desc, pkt)

// send ack
var writer = convert.binWriter()
writer.writeUint8(0x01)
if (pkt.desc == 'config' && requestConfig) {
    writer.writeUint8(0x43) // C
    writer.writeUint8(0x57) // W
} else if (requestConfig || event.endpoint.tags.config == 'report') {
    // request configuration from device
    writer.writeUint8(0x43) // C
    writer.writeUint8(0x52) // R
} else {
    // simple ack, no further action needed
    writer.writeUint8(0x41) // A
    writer.writeUint8(0x00)
}

writer.writeUint16(pkt.txnId)
if (pkt.desc == 'config' && requestConfig) {
    var keys = Object.keys(desiredConfig.values)
    var configWriter = convert.binWriter()
    var count = keys.length
    configWriter.writeUint8(count)
    log.trace('writing config', desiredConfig)
    for(var i = 0;i<count;i++) {
        writeVLString(configWriter,keys[i])
        writeVLString(configWriter,desiredConfig.values[keys[i]])
    }
    writer.writeUint8(1) // number of data elements
    writer.writeUint8(1) // data type
    writer.writeUint8(1) // data version

    var configBytes = configWriter.getBytes()
    writer.writeUint16(configBytes.length)
    writer.writeBytes(configBytes)
    
    endpoint.updateTags(event.endpoint.key, { config: 'update-in-progress' }, null)
    log.trace('sent configuration update', desiredConfig)
}

udp.send(event.service.key, event.data.remoteAddr, writer.getBytes())

exec.now('compact-binary-decoded', pkt)
