triggers.def:
if (event.endpoint) {
    aws_iot.publish('aws-iot-core', event.endpoint.key, 'uplink/' + event.endpoint.key, event.data.data)
} else {
    aws_iot.publish('aws-iot-core', 'no-endpoint-key', 'uplink/no-endpoint-key', event.data.data)
}

if (event.endpoint) {
    aws_iot.shadow_update('aws-iot-core', event.endpoint.key, 'data', undefined, event.data.data)
} else {
    aws_iot.shadow_update('aws-iot-core', 'no-endpoint-key', 'data', undefined, event.data.data)
}

// send an update on the job so AWS IoT Core keeps it alive.
aws_iot.job_update('aws-iot-core', event.data.thingName, event.data.jobId, 30, { progress: 50 })

// do work 
trigger.sleep(500)

// complete the job after it is done.  You can complete a job asynchronously if you store the jobId and send it as needed.
aws_iot.job_complete('aws-iot-core', event.data.thingName, event.data.jobId, true, { progress: 100 })
