var payloadIsString = false

var obj = Object.assign({}, event.data)

if(payloadIsString) {
    obj.payloadString = convert.binToStr(convert.hexToBin(event.data.payload))
}

exec.now('iridium-mo', obj)

var obj = {}
Object.assign(obj, event.data.data)
if(obj.messageStr) {
  obj.message = convert.binToHex(convert.strToBin(obj.message))
  delete obj.messageStr
}

aws_sqs.send('iridium-mt-sender',obj, {groupId:'deafult'})

var model = {
    key: event.data.body.data.mo_header.imei,
    name: event.data.body.data.mo_header.imei,
    location: {
        lat: event.data.body.data.location_information.latitude,
        lon: event.data.body.data.location_information.longitude
    },
    values: {}
}

// decode payload here.
try {
    var payload = convert.hexToBin(event.data.body.data.payload)
    model.values = convert.binToString(payload)
} catch(e) {

}

track.publish('iridium-track',event.data.body.data.mo_header.imei,event.ts,model)
